# coding=utf-8import wximport osdef input_analysis(input_str, tc):    tc.AppendText('\n')    if '增加' in input_str:        tc.AppendText('增加什么？')    else:        tc.AppendText('没听懂，请小主明示！')class DengShen(wx.Frame):    def __init__(self, parent, title):        super(DengShen, self).__init__(parent, title=title, size=(700, 500))        panel = wx.Panel(self)        hbox3 = wx.BoxSizer(wx.HORIZONTAL)        self.t3 = wx.TextCtrl(panel, size=(600, 1000), style=wx.TE_MULTILINE)        hbox3.Add(self.t3, 1, wx.EXPAND | wx.ALIGN_LEFT | wx.ALL, 5)        self.t3.Bind(wx.EVT_TEXT_ENTER, self.on_enter_pressed)        self.t3.SetBackgroundColour('Black'), self.t3.SetForegroundColour('Green')        panel.SetSizer(hbox3)        self.Centre()        self.Show()        self.Fit()    def on_key_typed(self, event):        print(event.GetString())    def on_enter_pressed(self, event):        input_analysis(event.GetString(), self.t3)        # self.t3.AppendText(event.GetString())        # result = os.popen(event.GetString())        # res = result.read()        # for line in res.splitlines():            # print(line)            # self.t3.AppendText(line)    def OnMaxLen(self, event):        print("Maximum length reached")if __name__ == '__main__':    app = wx.App()    DengShen(None, '灯神')    app.MainLoop()